SELECT TOP 120 CDP.*,  CLIVEN.Razonsocial as VendedorDesc,  CLICO1.Razonsocial as CuentaOrden1Desc,  CLICO2.Razonsocial as CuentaOrden2Desc,  CLICOR.Nombre as CorredorDesc,  CLIENT.Razonsocial as EntregadorDesc,  Articulos.Descripcion as Producto,  LOCORI.Nombre as ProcedenciaDesc,  LOCDES.Descripcion as DestinoDesc  FROM WilliamsMailFiltros CDP  LEFT OUTER JOIN Clientes CLIVEN ON CDP.Vendedor = CLIVEN.IdCliente  LEFT OUTER JOIN Clientes CLICO1 ON CDP.CuentaOrden1 = CLICO1.IdCliente  LEFT OUTER JOIN Clientes CLICO2 ON CDP.CuentaOrden2 = CLICO2.IdCliente  LEFT OUTER JOIN Vendedores CLICOR ON CDP.Corredor = CLICOR.IdVendedor  LEFT OUTER JOIN Clientes CLIENT ON CDP.Entregador = CLIENT.IdCliente  LEFT OUTER JOIN Articulos ON CDP.IdArticulo = Articulos.IdArticulo  LEFT OUTER JOIN Localidades LOCORI ON CDP.Procedencia = LOCORI.IdLocalidad  LEFT OUTER JOIN WilliamsDestinos LOCDES ON CDP.Destino = LOCDES.IdWilliamsDestino  
WHERE isnull(PuntoVenta,0)=1  ORDER BY IdWilliamsMailFiltro DESC 